def new_reading(reading):
    '''
    We were missing any readings after the first one.
    '''
    updated = re.sub(r'[ 　,](\w+)\[([\w、／/ 　。－・,-]*)\]', r'｜\1《\2》',
                     reading, flags=re.UNICODE)
    if reading != updated:
        print(reading, "  ====>  ", updated)
    return updated


